import { StyleSheet, Dimensions } from "react-native";

const {width, height} = Dimensions.get('window');

const styles = StyleSheet.create({
    container:{
        flex: 1, 
        justifyContent: 'center', 
        alignItems: 'center'
    },
    logoutButtonContainer: {
        flex: 1,
        justifyContent: 'flex-end',
    },
    logoutButton: {
        backgroundColor: 'white',
        height: 55,
        width: width/1.2,
        alignItems: 'center',
        justifyContent: 'center',
        borderRadius: 10,
        marginHorizontal: 20,
        marginVertical: 10,
        borderWidth: 1,
        borderColor: 'green',
        borderWidth: 3,
        shadowColor: "#000",
        shadowOffset: {
          width: 0,
          height: 4,
        },
        shadowOpacity: 0.25,
        shadowRadius: 3.84,
        elevation: 5,
    },
    logoutButtonText: {

    },
    profileContainer: {
        flex:2,
        height: height/1.3,
        width: width/1.3,
        position: 'absolute',
        borderRadius: 20,
        borderWidth: 4,
        borderColor: 'green',
        justifyContent: 'center',
        alignItems: 'center',
        paddingBottom: 80,
        backgroundColor: 'white',
        borderColor: 'green',
        borderWidth: 3,
        shadowColor: "#000",
        shadowOffset: {
          width: 0,
          height: 4,
        },
        shadowOpacity: 0.25,
        shadowRadius: 3.84,
        elevation: 5,
    },
    avatarContainer:{
        width: width/2,
        height: height/4,
        borderRadius: 32,
        justifyContent: 'center',
        alignItems: 'center',
        marginBottom: 20,
        paddingBottom: 20,
    },
    avatar: {
        width: 256,
        height: 256,
    },
    editText: {
        paddingLeft: 170,
        marginBottom: 20,
        paddingBottom: 20,
    },
    profileInfoContainer: {
        width: width/1.4,
        height: height/7,
        paddingTop: 30,
        alignItems: 'center',
    },
    textContainer:{
        alignItems: 'center',
        paddingTop: 10,
        paddingBottom: 10,
    },
    nameText: {
        fontSize:28,
        fontWeight: 'bold'
    },
    emailText:{
        fontSize: 18,
        color: 'black'
    }
})

export default styles
